import { useEffect, useState } from "react";
import { getTasks, createTask, deleteTask, updateTask } from "../api";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [employeeName, setEmployeeName] = useState("");
  const [errors, setErrors] = useState({
    newTask: "",
    newDescription: "",
    dueDate: "",
    employeeName: ""
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const handleAddTask = async () => {
    let isValid = true;
    let validationErrors = {
      newTask: "",
      newDescription: "",
      dueDate: "",
      employeeName: ""
    };

    if (!newTask.trim()) validationErrors.newTask = "Task Title is required";
    if (!newDescription.trim()) validationErrors.newDescription = "Task Description is required";
    if (!dueDate.trim()) validationErrors.dueDate = "Due Date is required";
    if (!employeeName.trim()) validationErrors.employeeName = "Employee Name is required";
    
    setErrors(validationErrors);

    if (Object.values(validationErrors).some((error) => error)) return;

    try {
      const task = await createTask({
        title: newTask,
        description: newDescription,
        status: "todo",
        dueDate: new Date(dueDate).toISOString(),
        employeeName,
      });

      setTasks([...tasks, task]);
      setNewTask("");
      setNewDescription("");
      setDueDate("");
      setEmployeeName("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const updatedTask = await updateTask(id, { status: newStatus });
      setTasks(tasks.map((task) => (task._id === id ? updatedTask : task)));
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  const today = new Date().toISOString().split('T')[0];
  const columns = {
    todo: "No tasks to start",
    "in-progress": "No tasks in progress",
    completed: "No completed tasks"
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh" }}>
      <h2>Task Manager</h2>

      {/* Task Input */}
      <div style={{ marginBottom: "1rem", display: "flex", flexDirection: "column", gap: "8px", width: "300px" }}>
        <input type="text" placeholder="Task Title" value={newTask} onChange={(e) => setNewTask(e.target.value)} />
        {errors.newTask && <span style={{ color: "red" }}>* {errors.newTask}</span>}
        <textarea placeholder="Task Description" value={newDescription} onChange={(e) => setNewDescription(e.target.value)} rows="3" />
        {errors.newDescription && <span style={{ color: "red" }}>* {errors.newDescription}</span>}
        <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} min={today} />
        {errors.dueDate && <span style={{ color: "red" }}>* {errors.dueDate}</span>}
        <input type="text" placeholder="Employee Name" value={employeeName} onChange={(e) => setEmployeeName(e.target.value)} />
        {errors.employeeName && <span style={{ color: "red" }}>* {errors.employeeName}</span>}
        <button onClick={handleAddTask}>Add Task</button>
      </div>

      {/* Kanban Board */}
      <div style={{ display: "flex", justifyContent: "center", gap: "20px", width: "90%" }}>
        {Object.keys(columns).map((status) => (
          <div key={status} style={{ width: "30%", background: "#f4f4f4", padding: "10px", borderRadius: "5px" }}>
            <h3>{status.replace("-", " ").toUpperCase()}</h3>
            {tasks.filter(task => task.status === status).length > 0 ? (
              tasks.filter(task => task.status === status).map(task => (
                <div key={task._id} style={{ background: "#ddd", padding: "10px", margin: "5px 0", borderRadius: "5px" }}>
                  <strong>{task.title}</strong>
                  <p>{task.description}</p>
                  <span>Due: {new Date(task.dueDate).toLocaleDateString("en-US")}</span>
                  <button onClick={() => handleDeleteTask(task._id)}>Delete</button>
                </div>
              ))
            ) : (
              <p>{columns[status]}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskList;
