import { useEffect, useState } from "react";
import { getTasks, createTask, deleteTask, updateTask } from "../api";

const TaskBoard = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [employeeName, setEmployeeName] = useState("");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const handleAddTask = async () => {
    if (!newTask.trim() || !newDescription.trim() || !dueDate.trim() || !employeeName.trim()) return;
    try {
      const task = await createTask({
        title: newTask,
        description: newDescription,
        status: "todo",
        dueDate: new Date(dueDate).toISOString(),
        employeeName,
      });
      setTasks([...tasks, task]);
      setNewTask("");
      setNewDescription("");
      setDueDate("");
      setEmployeeName("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const updatedTask = await updateTask(id, { status: newStatus });
      setTasks(tasks.map((task) => (task._id === id ? updatedTask : task)));
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  return (
    <div style={{ display: "flex", gap: "20px", padding: "20px" }}>
      {/* User Stories Column */}
      <div style={{ width: "20%", background: "#f5f5f5", padding: "10px", borderRadius: "5px" }}>
        <h3>User Stories</h3>
        <div style={{ background: "#d3c2d9", padding: "10px", marginBottom: "10px" }}>User Story 1</div>
        <div style={{ background: "#d3c2d9", padding: "10px", marginBottom: "10px" }}>User Story 2</div>
        <div style={{ background: "#d3c2d9", padding: "10px" }}>User Story 3</div>
      </div>

      {/* Task Board */}
      <div style={{ display: "flex", flex: 1, gap: "10px" }}>
        {[
          { status: "in-progress", title: "Tasks in Progress" },
          { status: "completed", title: "Tasks Completed" },
          { status: "closed", title: "Tasks Closed" }
        ].map(({ status, title }) => (
          <div key={status} style={{ flex: 1, background: "#f9f9f9", padding: "10px", borderRadius: "5px" }}>
            <h3 style={{ background: "#2c4b5a", color: "#fff", padding: "10px" }}>{title}</h3>
            {tasks
              .filter((task) => task.status === status)
              .map((task) => (
                <div key={task._id} style={{ background: "#635e6f", color: "#fff", padding: "10px", marginBottom: "5px", borderRadius: "5px" }}>
                  {task.title}
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskBoard;
