import { useEffect, useState } from "react";
import { getTasks, createTask, deleteTask, updateTask } from "../api";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [employeeName, setEmployeeName] = useState("");
  const [errors, setErrors] = useState({
    newTask: "",
    newDescription: "",
    dueDate: "",
    employeeName: "",
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const handleAddTask = async () => {
    let isValid = true;
    let validationErrors = {
      newTask: "",
      newDescription: "",
      dueDate: "",
      employeeName: "",
    };

    if (!newTask.trim()) validationErrors.newTask = "Task Title is required";
    if (!newDescription.trim()) validationErrors.newDescription = "Task Description is required";
    if (!dueDate.trim()) validationErrors.dueDate = "Due Date is required";
    if (!employeeName.trim()) validationErrors.employeeName = "Employee Name is required";

    setErrors(validationErrors);
    if (!Object.values(validationErrors).every((e) => e === "")) return;

    try {
      const task = await createTask({
        title: newTask,
        description: newDescription,
        status: "todo",
        dueDate: new Date(dueDate).toISOString(),
        employeeName,
      });

      setTasks([...tasks, task]);
      setNewTask("");
      setNewDescription("");
      setDueDate("");
      setEmployeeName("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const updatedTask = await updateTask(id, { status: newStatus });
      setTasks(tasks.map((task) => (task._id === id ? updatedTask : task)));
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  const today = new Date().toISOString().split("T")[0];
  
  const statuses = ["todo", "in-progress", "completed"];

  return (

    
    <div style={{ display: "flex", justifyContent: "center", padding: "20px", gap: "20px" }}>
      {statuses.map((status) => (
        <div key={status} style={{ width: "300px", background: "#f4f4f4", padding: "10px", borderRadius: "5px" }}>
          <h3 style={{ textAlign: "center", background: "#2c3e50", color: "white", padding: "10px", borderRadius: "5px" }}>
            {status.replace("-", " ").toUpperCase()}
          </h3>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {tasks.filter((task) => task.status === status).map((task) => (
              <li key={task._id} style={{ background: "#fff", padding: "10px", margin: "5px 0", borderRadius: "5px" }}>
                <strong>{task.title}</strong>
                <p style={{ fontSize: "14px", color: "#555" }}>{task.description}</p>
                <span>Employee: {task.employeeName}</span>
                <br />
                <span>Due Date: {new Date(task.dueDate).toLocaleDateString("en-US")}</span>
                <br />
                <span>Created: {new Date(task.createdAt).toLocaleDateString("en-US")}</span>
                <br />
                <select value={task.status} onChange={(e) => handleStatusChange(task._id, e.target.value)}>
                  {statuses.map((s) => (
                    <option key={s} value={s}>{s.replace("-", " ")}</option>
                  ))}
                </select>
                <button onClick={() => handleDeleteTask(task._id)} style={{ background: "red", color: "white", border: "none", padding: "5px", borderRadius: "3px", cursor: "pointer", marginTop: "5px" }}>
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default TaskList;
