import { useEffect, useState } from "react";
import { getTasks, createTask, deleteTask, updateTask } from "../api";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    status: "todo",
    dueDate: "",
    files: [],
    employeeName: "",
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const handleAddTask = async () => {
    if (!newTask.title.trim()) return;
    try {
      const task = await createTask(newTask);
      setTasks([...tasks, task]);
      setNewTask({ title: "", description: "", status: "todo", dueDate: "", files: [], employeeName: "" });
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const handleFileUpload = (e) => {
    setNewTask({ ...newTask, files: Array.from(e.target.files) });
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Task Manager</h2>

      {/* Input Fields */}
      <input type="text" placeholder="Title" value={newTask.title} onChange={(e) => setNewTask({ ...newTask, title: e.target.value })} />
      <input type="text" placeholder="Description" value={newTask.description} onChange={(e) => setNewTask({ ...newTask, description: e.target.value })} />
      <input type="date" value={newTask.dueDate} onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })} />
      <input type="text" placeholder="Employee Name" value={newTask.employeeName} onChange={(e) => setNewTask({ ...newTask, employeeName: e.target.value })} />
      <input type="file" multiple onChange={handleFileUpload} />

      <button onClick={handleAddTask}>Add Task</button>

      {/* Task List */}
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.description} <br />
            Status: {task.status} | Due: {new Date(task.dueDate).toLocaleDateString()} | Employee: {task.employeeName}
            <br />
            {task.files.map((file, index) => (
              <a key={index} href={file} target="_blank" rel="noopener noreferrer">
                View File
              </a>
            ))}
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
