import { useEffect, useState } from "react";
import { getTasks, createTask, deleteTask, updateTask } from "../api";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [newDescription, setNewDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [employeeName, setEmployeeName] = useState("");
  const [errors, setErrors] = useState({
    newTask: "",
    newDescription: "",
    dueDate: "",
    employeeName: "",
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const handleAddTask = async () => {
    let validationErrors = {
      newTask: newTask.trim() ? "" : "Task Title is required",
      newDescription: newDescription.trim() ? "" : "Task Description is required",
      dueDate: dueDate.trim() ? "" : "Due Date is required",
      employeeName: employeeName.trim() ? "" : "Employee Name is required",
    };

    setErrors(validationErrors);
    if (!Object.values(validationErrors).every((e) => e === "")) return;

    try {
      const task = await createTask({
        title: newTask,
        description: newDescription,
        status: "todo",
        dueDate: new Date(dueDate).toISOString(),
        employeeName,
      });

      setTasks([...tasks, task]);
      setNewTask("");
      setNewDescription("");
      setDueDate("");
      setEmployeeName("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(tasks.filter((task) => task._id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      const updatedTask = await updateTask(id, { status: newStatus });
      setTasks(tasks.map((task) => (task._id === id ? updatedTask : task)));
    } catch (error) {
      console.error("Error updating status:", error);
    }
  };

  const today = new Date().toISOString().split("T")[0];
  const statuses = ["todo", "in-progress", "completed"];

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh" }}>
      <h2>Task Manager</h2>

      <div style={{ marginBottom: "1rem", display: "flex", flexDirection: "column", gap: "8px", width: "300px" }}>
        <input type="text" placeholder="Task Title" value={newTask} onChange={(e) => setNewTask(e.target.value)} style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }} />
        {errors.newTask && <span style={{ color: "red", fontSize: "12px" }}>* {errors.newTask}</span>}

        <textarea placeholder="Task Description" value={newDescription} onChange={(e) => setNewDescription(e.target.value)} rows="3" style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }} />
        {errors.newDescription && <span style={{ color: "red", fontSize: "12px" }}>* {errors.newDescription}</span>}

        <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }} min={today} required />
        {errors.dueDate && <span style={{ color: "red", fontSize: "12px" }}>* {errors.dueDate}</span>}

        <input type="text" placeholder="Employee Name" value={employeeName} onChange={(e) => setEmployeeName(e.target.value)} style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }} />
        {errors.employeeName && <span style={{ color: "red", fontSize: "12px" }}>* {errors.employeeName}</span>}

        <button onClick={handleAddTask} style={{ padding: "8px 12px", borderRadius: "5px", background: "#4CAF50", color: "#fff", border: "none", cursor: "pointer" }}>Add Task</button>
      </div>

      <div style={{ display: "flex", justifyContent: "center", padding: "20px", gap: "20px" }}>
        {statuses.map((status) => (
          <div key={status} style={{ width: "300px", background: "#f4f4f4", padding: "10px", borderRadius: "5px" }}>
            <h3 style={{ textAlign: "center", background: "#2c3e50", color: "white", padding: "10px", borderRadius: "5px" }}>{status.replace("-", " ").toUpperCase()}</h3>
            <ul style={{ listStyle: "none", padding: 0 }}>
              {tasks.filter((task) => task.status === status).length === 0 ? (
                <p style={{ textAlign: "center", color: "#888" }}>{status === "todo" ? "No tasks to start" : status === "in-progress" ? "No tasks in progress" : "No completed tasks"}</p>
              ) : (
                tasks.filter((task) => task.status === status).map((task) => (
                  <li key={task._id} style={{ background: "#fff", padding: "10px", margin: "5px 0", borderRadius: "5px" }}>
                    <strong>{task.title}</strong>
                    <p style={{ fontSize: "14px", color: "#555" }}>{task.description}</p>
                    <span>Employee: {task.employeeName}</span>
                    <br />
                    <span>Due Date: {new Date(task.dueDate).toLocaleDateString("en-US")}</span>
                    <br />
                    <span>Created: {new Date(task.createdAt).toLocaleDateString("en-US")}</span>
                    <br />
                    <select value={task.status} onChange={(e) => handleStatusChange(task._id, e.target.value)}>{statuses.map((s) => (<option key={s} value={s}>{s.replace("-", " ")}</option>))}</select>
                    <button onClick={() => handleDeleteTask(task._id)} style={{ background: "red", color: "white", border: "none", padding: "5px", borderRadius: "3px", cursor: "pointer", marginTop: "5px" }}>Delete</button>
                  </li>
                ))
              )}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskList;
